// Shows.jsx
// Displays all shows

import React, { useMemo } from 'react';

// Components
import Grid from '../../components/Grid/Grid';

import './Shows.css';

const Shows = () => {

    const shows = [
        { showTitle: "Show 1", showDate: "2020-01-01" },
        { showTitle: "Show 2", showDate: "2020-01-02" },
        { showTitle: "Show 3", showDate: "2020-01-03" },
    ]

    // Define the columns for the table
    const columnDefs = [
        { field: "showTitle", headerName: "Show" },
        { field: "showDate", headerName: "Date" },
    ];

    // Define the default column properties
    const defaultColDef = useMemo(() => {
        return {
            sortable: true,
            filter: true,
            resizable: true
        };
    }, []);

    const gridOptions = {
        columnDefs: columnDefs, // Define the columns for the table
        defaultColDef: defaultColDef, // Define the default column properties
        pagination: true, // Enable pagination
        paginationPageSize: 10, // Set the number of rows per page
        cacheBlockSize: 10, // Set the number of rows per block to load
        animateRows: true, // Enable row animations
        rowData: shows, // Set the data for the table
        rowSelection: 'multiple', // Enable multiple row selection
        onGridReady: function (params) {
            params.api.sizeColumnsToFit(); // Resize the columns to fit the window on grid load
        },
        tooltipShowDelay: 0
    };

    return (
        <div>
            <div className="pageTitle">
                <h1>Shows</h1>
            </div>
            <div className="allShows">
                <Grid options={gridOptions}/>
            </div>
        </div>
    );
}

export default Shows;
